mapper.select:
select k.id,u.id as authorId,u.name as authorName, u.phone as authorPhone,k.date,k.title,k.count,k.intro,k.imageUrl,k.hot
from user u,know k
where u.id = k.uid and k.state = #{state}

  and u.name like "%"#{authorName}"%"


    and u.phone = #{authorPhone}


    and n.title  like "%"#{title}"%"

limit #{start},#{size}

select count(*)
from user u,know k
where u.id = k.uid and k.state = #{state}

    and u.name like "%"#{authorName}"%"


    and u.phone = #{authorPhone}


    and n.title  like "%"#{title}"%"
