mapper.select:
select n.id,u.id as authorId,u.name as authorName, u.phone as authorPhone,n.date,n.title,n.count,n.intro,n.imageUrl,n.hot
from user u,news n
where u.id = n.uid and n.state = #{state}

  and u.name like "%"#{authorName}"%"


    and u.phone = #{authorPhone}


    and n.title  like "%"#{title}"%"

limit #{start},#{size}

select count(*)
from user u,news n
where u.id = n.uid and n.state = #{state}

    and u.name like "%"#{authorName}"%"


    and u.phone = #{authorPhone}


    and n.title  like "%"#{title}"%"
